.navbar {
  background-color: #444;
  * {
    :hover,
    :active {
      text-decoration: none;
    }
  }
  // a {
  //   color: #666;
  // }
  .active {
    color: #007bff;
  }
  .navbar-brand a {
    color: #666;
  }
  .dropdown-item:active {
    color: #212529;
    background-color: #fff;
  }
}

.nav-links_spacing {
  list-style: none;
  margin-left: 3rem;
  margin-top: 1.25rem;
  
  font-weight: 400;
  font-size: 1.5rem;
}
